import React, { Component } from 'react'
import api from '../api'

import { Input, Button, DatePicker } from 'antd';
import moment from 'moment';
import 'antd/dist/antd.css';
import '../style/pages.css'

class BlogUpdate extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            title: '',
            time: '',
            content: '',
        }
    }

    handleChangeInputTitle = async event => {
        const title = event.target.value
        this.setState({ title })
    }

    handleChangeInputTime = (date,dateString) => {
        const time = dateString
        this.setState({ time })
    }

    handleChangeInputContent = async event => {
        const content = event.target.value
        this.setState({ content })
    }

    handleUpdateBlog = async () => {
        const { id, title, time, content } = this.state
        const payload = { title, time, content }

        await api.updateBlogById(id, payload).then(res => {
            window.alert(`Blog updated successfully`)
            this.setState({
                title: '',
                time: '',
                content: '',
            })
        })
    }

    componentDidMount = async () => {
        const { id } = this.state
        const blog = await api.getBlogById(id)

        this.setState({
            title: blog.data.data.title,
            time: blog.data.data.time,
            content: blog.data.data.content,
        })
    }

    render() {
        const { title, time, content } = this.state
        return (
            <div className="list">
                <h2>Create Blog</h2>

                <p>Title: </p>
                <Input
                    type="text"
                    value={title}
                    onChange={this.handleChangeInputTitle}
                />

                <p>Time: </p>
                <DatePicker onChange={this.handleChangeInputTime} 
                defaultValue={moment(time, 'YYYY-MM-DD')} format='YYYY-MM-DD' />

                <p>Content: </p>
                <Input
                    type="text"
                    value={content}
                    onChange={this.handleChangeInputContent}
                />
                <Button type="primary" onClick={this.handleIncludeBlog}>Update Blog</Button>
                <Button className="btn" type="primary" danger href={'/blog/list'}>Cancel</Button>
            </div>
        )
    }
}

export default BlogUpdate
