import React, { Component } from 'react'
import api from '../api'

import { List, Button } from 'antd';

class UpdateBlog extends Component {
    updateUser = event => {
        event.preventDefault()
        window.location.href = `/blog/update/${this.props.id}`
    }
    render() {
        return <div>onClick={this.updateUser}Update</div>
    }
}

class DeleteBlog extends Component {
    deleteUser = event => {
        event.preventDefault()
        if (
            window.confirm(
                `Do tou want to delete the Blog ${this.props.id} permanently?`,
            )
        ) {
            api.deleteBlogById(this.props.id)
            window.location.reload()
        }
    }
    render() {
        return <div>onClick={this.deleteUser}Delete</div>
    }
}

class BlogList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            blog: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllBlog().then(blog => {
            this.setState({
                blog: blog.data.data,
                isLoading: false,
            }) 
        })
    }

    render() {
        const { blog, isLoading } = this.state
        console.log('->', blog)

        let showTable = true
        if (!blog.length) {
            showTable = false
        }

        return (
            <List
            className="demo-loadmore-list"
            itemLayout="horizontal"
            dataSource={blog}
            renderItem={item => (
              <List.Item
                actions={[<a key={UpdateBlog} id={blog._id}>edit</a>, <a key="list-loadmore-more">delete</a>]}
              >
                  <List.Item.Meta
                    title={<a href="https://ant.design">{item.title}</a>}
                    description={item.content}
                  />
              </List.Item>
            )}
          />
        )
    }
}

export default BlogList
