import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class BlogInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            title: '',
            time: '',
            content: '',
        }
    }

    handleChangeInputTitle = async event => {
        const title = event.target.value
        this.setState({ title })
    }

    handleChangeInputTime = async event => {
        const time = event.target.time
        // event.target.validity.valid
        //     ? event.target.value
        //     : this.state.rating

        this.setState({ time })
    }

    handleChangeInputContent = async event => {
        const content = event.target.value
        this.setState({ content })
    }

    handleIncludeBlog = async () => {
        const { title, time, content } = this.state
        const payload = { title, time, content }

        await api.insertBlog(payload).then(res => {
            window.alert(`Blog inserted successfully`)
            this.setState({
                title: '',
                time: '',
                content: '',
            })
        })
    }

    render() {
        const { title, time, content } = this.state
        return (
            <div className="list">
                <h2>Create Blog</h2>

                <Label>Title: </Label>
                <InputText
                    type="text"
                    value={title}
                    onChange={this.handleChangeInputTitle}
                />

                <Label>Time: </Label>
                <InputText
                    type="text"
                    value={time}
                    onChange={this.handleChangeInputTime}
                />

                <Label>Content: </Label>
                <InputText
                    type="text"
                    value={content}
                    onChange={this.handleChangeInputContent}
                />

                <Button onClick={this.handleIncludeBlog}>Add Blog</Button>
                <CancelButton href={'/blog/list'}>Cancel</CancelButton>
            </div>
        )
    }
}

export default BlogInsert
