import React, { Component } from 'react'
import api from '../api'

import { List, Button, Space } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';
import '../style/pages.css'
import 'antd/dist/antd.css';

class UpdateBlog extends Component {
    updateUser = event => {
        event.preventDefault()
        window.location.href = `/blog/update/${this.props.id}`
    }
    render() {
        return <div>onClick={this.updateUser}Update</div>
    }
}

class DeleteBlog extends Component {
    deleteUser = event => {
        event.preventDefault()
        if (
            window.confirm(
                `Do tou want to delete the Blog ${this.props.id} permanently?`,
            )
        ) {
            api.deleteBlogById(this.props.id)
            window.location.reload()
        }
    }
    render() {
        return <div>onClick={this.deleteUser}Delete</div>
    }
}

class BlogList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            blog: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllBlog().then(blog => {
            this.setState({
                blog: blog.data.data,
                isLoading: false,
            }) 
        })
    }

    render() {
        const { blog, isLoading } = this.state
        console.log('->', blog)
        const IconText = ({ icon, text }) => (
            <Space>
              {React.createElement(icon)}
              {text}
            </Space>
          );

        let showTable = true
        if (!blog.length) {
            showTable = false
        }

        return (

                <List
                className="blog-list"
                itemLayout="vertical"
                dataSource={blog}
                loading={isLoading}

                renderItem={item => (
                    <List.Item
                    key={item.title}
                    actions={[
                        <IconText icon={StarOutlined} text="156" key="list-vertical-star-o" />,
                        <IconText icon={LikeOutlined} text="156" key="list-vertical-like-o" />,
                        <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
                    ]}
                    >
                    <List.Item.Meta
                        title={<a href={item.href}>{item.title}</a>}
                        description={item.time}
                    />
                    {item.content}
                    </List.Item>
                )}
            />

        )
    }
}

export default BlogList
