import React, { Component } from 'react'
import api from '../api'

import { List, Button } from 'antd';
import '../style/pages.css'
import 'antd/dist/antd.css';

class UpdateBlog extends Component {
    updateUser = event => {
        event.preventDefault()
        window.location.href = `/blog/update/${this.props.id}`
    }
    render() {
        return <div>onClick={this.updateUser}Update</div>
    }
}

class BlogList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            blog: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllBlog().then(blog => {
            this.setState({
                blog: blog.data.data,
                isLoading: false,
            }) 
        })
    }

    render() {
        const { blog, isLoading } = this.state
        console.log('->', blog)

        let showTable = true
        if (!blog.length) {
            showTable = false
        }

        const deleteBlog = (blog) => {
            if (
                window.confirm(
                    `Do tou want to delete the blog ${blog.title} permanently?`,
                )
            ) {
                api.deleteBlogById(blog._id)
                window.location.reload()
            }
        }
        const updateBlog = (id) => {
            window.location.href = `/blog/update/${id}`
        }

        return (
            <div className="list">
            <List
                className="blog-list"
                itemLayout="vertical"
                dataSource={blog}
                loading={isLoading}

                renderItem={item => (
                    <List.Item key={item.title}>
                        <List.Item.Meta
                            title={<a href={item.href}>{item.title}</a>}
                            description={item.time}
                        />
                        {item.content}

                    <span className="list-btn">
                        <Button type="primary" onClick={()=>{updateBlog(item._id)}}>Modify</Button>&nbsp;
                        <Button type="primary" danger onClick={()=>{deleteBlog(item)}}>Delete</Button>
                    </span>

                    </List.Item>
                )}
            />


            </div>
        )
    }
}

export default BlogList
