import React, { Component } from 'react'
import { List } from 'antd';

class BlogList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            blog: [{
                id: "001",
                title: "abc",
                time: "abc",
                description: "abc",
                content: "abc"
            }],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        this.setState({
            blog: blog.data.data,
            isLoading: false,
        }) 
    }

    render() {
        const { blog, isLoading } = this.state

        let showTable = true
        if (!blog.length) {
            showTable = false
        }

        return (
            <div className="list">
            <List
                className="blog-list"
                itemLayout="vertical"
                dataSource={blog}
                loading={isLoading}

                renderItem={item => (
                    <List.Item key={item.title}>
                        <List.Item.Meta
                            title={<a href={`/blog/detail/${item._id}`}>{item.title}</a>}
                            description={item.time}
                        />
                    <p>{item.description}</p>
                    </List.Item>
                )}
            />


            </div>
        )
    }
}

export default BlogList
