import React, { Component } from 'react'
import api from '../api'
import { Input, Button, DatePicker, Collapse } from 'antd';
import 'antd/dist/antd.css';
import '../style/pages.css'

class BlogInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            title: '',
            time: '',
            content: '',
        }
    }

    handleChangeInputTitle = async event => {
        const title = event.target.value
        this.setState({ title })
    }

    handleChangeInputTime = (dateString) => {
        const time = dateString
        this.setState({ time })
    }

    handleChangeInputContent = async event => {
        const content = event.target.value
        this.setState({ content })
    }

    handleIncludeBlog = async () => {
        const { title, time, content } = this.state
        const payload = { title, time, content }

        await api.insertBlog(payload).then(res => {
            window.alert(`Blog inserted successfully`)
            this.setState({
                title: '',
                time: '',
                content: '',
            })
        })
    }

    render() {
        const { title, time, content } = this.state
        return (
            <div className="list">
                <h2>Create Blog</h2>

                <p>Title: </p>
                <Input
                    type="text"
                    value={title}
                    onChange={this.handleChangeInputTitle}
                />

                <p>Time: </p>
                <DatePicker onChange={this.handleChangeInputTime} />

                <p>Content: </p>
                <Input
                    type="text"
                    value={content}
                    onChange={this.handleChangeInputContent}
                />
                <Button type="primary" onClick={this.handleIncludeBlog}>Add Blog</Button>
                <Button className="btn" type="primary" danger href={'/blog/list'}>Cancel</Button>
            </div>
        )
    }
}

export default BlogInsert
